import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import BackEndBookCard from '../BookIndex/BackEndBookCard'
import { Card, Button, Image, Icon} from 'semantic-ui-react'

export default function Home(){

    const[books,setBooks] = useState([])
    const[backEndWaitings, setBackEndWaitings] = useState([])
    const[users, setUsers] = useState([])
    const[index, setIndex] = useState(0)

    let waitings = []
    let waitingsMapped = []
    let sponsorsMapped = []
    let topReaders = []
    let topReadersMapped = []

    useEffect(()=>{
        fetch(`http://localhost:3000/books`)
        .then(response=>response.json())
        .then(data=>{setBooks(data)})

        fetch(`http://localhost:3000/users`)
        .then(response=>response.json())
        .then(data=>{setUsers(data)})

        fetch(`http://localhost:3000/waitings`)
        .then(response=>response.json())
        .then(data=>{setBackEndWaitings(data)})
    },[])




     if (books.length > 0){
        for(let i=0;i < books.length;i++){
            let waitingsFulFilled = books[i].waitings.filter(waitings=>waitings.fulfilled!==false).length

            waitings.push({[waitingsFulFilled]:books[i]})
        }
        waitings.sort(function (a, b){
            return Object.keys(b)[0] - Object.keys(a)[0]
        })
        waitingsMapped = waitings.slice(index, (index+8)).map(waiting=>{
            return( <BackEndBookCard book = {Object.values(waiting)[0]}/>)
        })
    }

    if (backEndWaitings.length > 0){ 
        let sponsoredWaitings = backEndWaitings.filter(waiting=>waiting.fulfilled==true)
        let sponsorAOH=[]
        for (let i=0;i<users.length;i++){
            let userSponsors = sponsoredWaitings.filter(waiting=>waiting.sponsor_id===users[i].id)
            sponsorAOH.push({[userSponsors.length]:users[i]})
        }
        let x = sponsorAOH.sort(function (a, b){
            return Object.keys(b)[0] - Object.keys(a)[0]
        })
        sponsorsMapped = x.slice(0,4).map(sponsor=>{
            return( 
                    <Card>
                        <Image src={Object.values(sponsor)[0].profile_pic? Object.values(sponsor)[0].profile_pic: "https://www.xovi.com/wp-content/plugins/all-in-one-seo-pack/images/default-user-image.png"} size='mini' circular />
                        <Card.Content>
                            <Card.Header><Link to={`/otheruserpage/${Object.values(sponsor)[0].id}`} >{Object.values(sponsor)[0].username} </Link></Card.Header>
                            {/* <Card.Meta>
                                <span className='date'>Joined in 2015</span>
                            </Card.Meta> */}
                        </Card.Content>
                            <Card.Content extra>
                                <a>
                            <Icon name='user' />
                             {Object.keys(sponsor)[0]} sponsors
                            </a>
                        </Card.Content>
                    </Card>


                    // <p>sponsors: {Object.keys(sponsor)[0]}</p>


                )
        })
    }
    if (users.length > 0){
       topReaders = users.sort(function (a, b){
           debugger
            return  (b-waitings.filter(waiting=>waiting.fulfilled===true).length)- (a.waitings.filter(waiting=>waiting.fulfilled===true).length)
        })  
        console.log(topReaders)
        topReadersMapped = topReaders.slice(0,4).map(reader=>{
            return( 
                    <Card>
                        <Image src={reader.profile_pic ? reader.profile_pic : "https://www.xovi.com/wp-content/plugins/all-in-one-seo-pack/images/default-user-image.png"} size='mini' circular />
                        <Card.Content>
                            <Card.Header><Link to={`/otheruserpage/${reader.id}`} >{reader.username} </Link></Card.Header>
                        </Card.Content>
                            <Card.Content extra>
                                <a>
                            <Icon name='book' />
                             {reader.waitings.filter(waiting=>waiting.fulfilled===true).length } Books Read
                            </a>
                        </Card.Content>
                    </Card>




                )
        })

    }

    return(
        <>
            <div className="home-info">
                <h2 className="who">
                    Who we are
                </h2>
                <p style={{fontSize: '15px'}}>
                    If you've ever read an impactful book that you wish more people would also read, BookFund is the place for you!
                    <br></br>
                    BookFund is a platform where users can sponsor the books they love, sharing the knowledge  with others who may have less resources. 
                    <br></br>
                    Users can sign up on a waitlist for a book to receive the next sponsored copy of that book. After receiving a book, users must publish a review of the book on the site, as well as enter a two week wait period before they are eligible to receive their next book.
                </p>
            </div>

                <h2 className="pop" >Popular Now</h2>
            <div className="popular-now">
                {waitingsMapped.length >0 ? 
                <Card.Group itemsPerRow={8}>
                    {waitingsMapped}  
                </Card.Group>
                :null} 
            </div>
                <div className="buttons-combined">
                    <Button className="next-back-button"  onClick={()=>setIndex(index+8)}>Next</Button>
                    {index > 0 ? <Button className="next-back-button" onClick={()=>setIndex(index-8)}>Back</Button>: null}
                </div>
            <div style={{display:'flex',marginTop: '50px'}}className="bottom-home">
                 <div className="top-readers-home">
                    <h4>Top Readers:</h4>
                    <Card.Group itemsPerRow={4}>
                        {topReadersMapped.length>0 ? topReadersMapped :null}
                    </Card.Group>
                </div>

                <div className="top-sponsors-home">
                    <h4>Top Sponsors:</h4>
                    <Card.Group itemsPerRow={4}>
                        {sponsorsMapped.length>0 ? sponsorsMapped :null}
                    </Card.Group>
                </div>




            </div>
        </>
    )
}
