                    <Card.Group itemsPerRow={8}>
                        {sponsoredMapped.slice(sponsoredIndex, sponsoredIndex+8)}  
                    </Card.Group>
                </div>

                <Button className="next-back-button"  onClick={()=>setSponsoredIndex(sponsoredIndex+8)}>Next</Button>
