import React,{useEffect, useState} from "react"
import { useHistory } from "react-router-dom";
import { Button, Input, Menu, Header, Image, Modal, Form } from 'semantic-ui-react'

export default function LogIn ({setUser}){

    let history = useHistory()

    const [open, setOpen] = useState(false)
    const [formState, setFormState] = useState({})

    function handleChange(e){
        setFormState({
            [e.target.name]: e.target.value
        })
    }

    function handleSubmit(e){
        e.preventDefault()
        let confObj = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formState),
        }

        fetch('http://localhost:3000/users/login', confObj)
        .then(response=>response.json())
        .then(data=>{
            console.log(data)
            setUser(data)      
            history.push('/userpage')
        })

    }

    return (
        <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button>Log In</Button>}
    >
      <Modal.Header>Log In</Modal.Header>
      <Modal.Content >
           <div className="form ">
                <form className="signup" onSubmit={(e)=>handleSubmit(e)}>
                    <label>
                        Username:
                        <input type="text" name="username" onChange={(e)=>handleChange(e)}/>
                    </label>
                        <input type="submit" value="Login" className="button"/>
                </form>
                <Form>
                    <Form.Field>
                        <label>Username</label>
                        <input placeholder='Username' />
                    </Form.Field>
                    <Form.Field>
                        <label>Password</label>
                        <input placeholder='Password' />
                    </Form.Field>
                    <Button type='submit' style={{float:'right', marginBottom: '5px'}}>Login</Button>
                </Form>

            </div>





      </Modal.Content>
    </Modal>

    )
}
