import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function BookPage({setSavedBooks, savedBooks}){

    const[book, setBook] = useState (null)
    const params = useParams()
    let foundBook = null


    useEffect(()=>{
        foundBook = savedBooks.find(book=>book.api_id===params.id)
        setBook(foundBook)

        if (book === null ){
            fetch(`https://www.googleapis.com/books/v1/volumes/${params.id}`)
            .then(response => response.json())
            .then(data=>{
                setBook({
                    title: book.title, 
                    subtitle: book.subtitle, 
                    authors: book.volumeInfo.authors, 
                    publisher: book.volumeInfo.publisher, 
                    publishedDate: book.volumeInfo.publishedDate , 
                    description: book.volumeInfo.description, 
                    image_url: book.volumeInfo.image_url, 
                    api_id: book.id,
                    waitings: false,
                })
                console.log(book)
            })

        }

    },[])








    // function waitListRequestAndStoreInDBRequest (){
    //     let confObj = {
    //         method: 'POST',
    //         headers: {
    //             'Content-Type': 'application/json',
    //         },
    //         body: JSON.stringify(formState),
    //     }

    //     fetch('http://localhost:3000/users', confObj)
    //     .then(response=>response.json())
    //     .then(data=>{
    //         console.log(data)
    //         if (data.error){
    //             alert("Username must be unique")
    //         }
    //         else{
    //             setUser(data)
    //         }
    //     })
    // }


    return(
        <>

            <img src={ book ? book.image_url: null }></img>
            <h2>{book? book.title: null}</h2>
            <h3>{book? book.subtitle: null}</h3>
            <p>by: {book? book.authors: null} ({book? book.publishedDate: null}) </p>
            <p>Publishing House: {book? book.publisher: null}</p>
            <p>Description: {book? book.description: null} </p>
            {/* <p>Waitlist:{book.waitings ?  book.waitings : "No on is waiting on this book yet"}</p> */}

            {/* <button onClick={waitListRequestAndStoreInDBRequest}>Jump on the Waitlist for this book</button> */}

        </>
    )

}
