import { useEffect, useState } from "react";


export default function Home({user}){

    const[books,setBooks] = useState([])
    let waitings = []
    let waitingsMapped = []

    useEffect(()=>{
        fetch(`http://localhost:3000/books`)
        .then(response=>response.json())
        .then(data=>{setBooks(data)})
    },[])

     if (books.length > 0){
        // for(let i=0;i < books.length;i++){
        //     waitings.push ( {[books[i].title]: books[i].waitings.filter(waiting=>waiting.fulfilled!== true)})
        // }
        // waitings.sort(function (a, b) {
        // return Object.values(b)[0].length - Object.values(a)[0].length  
        // })
        // console.log(waitings)

        // waitingsMapped= waitings
        // .filter(waiting=>Object.values(waiting)[0].length > 0)
        // .map(waiting=>{
        //     return (<li>{Object.keys(waiting)[0]}</li>)
        //     })

        for(let i=0;i < books.length;i++){
            let waitingsUnfulfilled = books[i].waitings.filter(waitings=>waitings.fulfilled!==true).length
            waitings.push({[waitingsUnfulfilled]:books[i]})
        }
        waitings.sort(function (a, b){
            return Object.values(b)[0].length - Object.values(a)[0].length  
        })

    }

    return(
        <>
            <h4>Top Books People are Waiting on:</h4>
            <ol>{waitingsMapped.length>0 ? waitingsMapped :null} </ol>
        </>
    )
}
