import React, { useState, useEffect } from "react"

export default function UserPage({user}){

    const[userWaitings, setUserWaitings] = useState([])
    const [savedBooks, setSavedBooks] = useState([])
    let waitingsMapped = []

    useEffect(()=>{
        fetch(`http://localhost:3000/books`)
        .then(response=>response.json())
        .then(data=>setSavedBooks(data))
    },[])

    if (user){


    //     let waitlistMapped = waitingsUnfulfilled.map(waiting=>{
    //         return <li>a</li>
    //     })


    let waitingsUnfulfilled = user.waitings.filter(waiting=>waiting.fulfilled!==true)
    waitingsUnfulfilled.map(waiting=>{
        let foundCopy = savedBooks.find(saved_book=> saved_book.id = waiting.book_id)
        waitingsMapped.push(foundCopy)
    })
    console.log(waitingsMapped)









    return(
        <div> 
            <img src={user.profile_pic ? user.profile_pic : null}></img>
            <p>name:{user.name ? user.name : null}</p>
            <p>email:{user.email ? user.email : null}</p>
            <p>username:{user.username ? user.username : null }</p>
            <p>bio:{user.bio ? user.bio : null}</p>
            <p>waitlist</p>
            <ul></ul>
            <p>sponsored list</p>
            <p>received list</p>
        </div>
    )}
    else{
        return null
    }
}
