    function fetchBooks(searchInput){
        let searchTerms = searchInput.replace(/\s/g, '+')
        fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchTerms}`)
        .then(response=>response.json())
        .then(data=>{
            setBooksFromSearch(data)
            console.log(data.items)
            history.push(`/bookindex`)
        })
    }


    if (booksFromSearch){
        let booksMapped = booksFromSearch.map(book=>{
            return(
                <div>
                    <h1>book.title</h1>
                </div>
            )
        })
    }

    return (
        <>
        {booksFromSearch ? booksMapped: null}  
        </>
    )
}
